import qrcode
from PIL import Image

def generate_qr_code(data_url, output_file='qr_code.png'):
    
    qr = qrcode.QRCode(
        version=1, 
        error_correction=qrcode.constants.ERROR_CORRECT_L,  
        box_size=10,  
        border=4,  
    )
    
    # Add the Data URL to the QR Code
    qr.add_data(data_url)
    qr.make(fit=True)


    img = qr.make_image(fill_color="black", back_color="white")


    img.save(output_file)
    print(f"QR code generated and saved as {output_file}")


if __name__ == "__main__":

    output_file='qr_code.png'
    data_url = "data:text/html;base64,"
    generate_qr_code(data_url)
